// ==UserScript==
// @name         Save As Text
// @name:zh      保存为纯文本文件
// @supportURL   https://github.com/rectcircle/save-as-text
// @namespace    https://github.com/rectcircle/save-as-text
// @license      MIT
// @version      1.0.1
// @description         Save the Page as Plain text file
// @description:zh      将当前网页保存为纯文本文件
// @author       Rectcircle <rectcircle96@gmail.com>
// @icon         data:image/png;base64,
// @include      *
// @run-at      document-end
// ==/UserScript==

/**
 * 以utf8编码将content保存到文件系统中，文件名为fileName
 * @param {string} fileName 文件名
 * @param {String} content 文件内容
 */
function saveAsText(fileName, content) {
	blobOptions = {
		type: "text/plain;charset=utf-8"
	};

	//创建Blob对象
	var pf = window.navigator.platform;
	if (pf == "Win32" || pf == "Windows") {
		content = '\uFEFF' + content;
	}
	var blob = new Blob([content], blobOptions);

	//使用a标签下载文件
	var a = document.createElement('a');

	//设置文件名
	a.innerHTML = fileName;
	a.download = fileName;

	//设置下载内容
	a.href = URL.createObjectURL(blob);

	//将此此节点放入body下
	document.body.appendChild(a);

	//触发点击事件
	var evt = document.createEvent("MouseEvents");
	evt.initEvent("click", false, false);
	a.dispatchEvent(evt);

	//删除标签
	document.body.removeChild(a);
}
//图标
var iconBase64 = "data:image/png;base64,";
/**
 * 添加一个下载按钮到页面
 */
function addButtonToBody() {
	var img = document.createElement('img');
	//指定id
	img.id = "__saveAsTextButton__"
	//指定图片
	img.src = iconBase64;
	//配置添加到页面的元素的样式
	img.style = "position:fixed; right:20px; bottom:20px; width:50px; " + //设置位置大小
		"z-index:2147483647; cursor:pointer; " + //设置在最上层，手型
		"border: 2px solid #999; border-radius: 70px; padding:10px; " + //边框
		"background-color:#222; opacity: 0.8;"; //颜色与透明度
	img.alt = "Save as Text";
	img.title = "Save as Text"
	//单击事件，保存当前页面
	img.onclick = function () {
		var fileName = document.title + ".txt";
		var content = document.body.innerText;
		// 下载选中的链接
		var selection = window.getSelection()
		if (selection !== null && selection.rangeCount !== 0) {
			var selectDoc = selection.getRangeAt(0).cloneContents()
			/**  @returns {string[]} */
			var getAllHrefs = (node) => {
				var result = []
				for (var i = 0; i < node.children.length; i++) {
					var child = node.children[i]
					if (child.tagName === 'A') {
						result.push(child.href)
					} else {
						result.push(...getAllHrefs(child))
					}
				}
				return result
			}
			/** @type {string[]} */
			var hrefBodys = []
			var selectHrefs = getAllHrefs(selectDoc)
			var hrefFetchPromise = selectHrefs.map(href => fetch(href))
			Promise.all(hrefFetchPromise).then(responses => {
				Promise.all(responses.map(r => r.text())).then(hrefHTMLs => {
					var hrefTexts = hrefHTMLs.map(htmlString => {
						let domparser = new DOMParser()
						var doc = domparser.parseFromString(htmlString, 'text/html');
						doc.querySelectorAll('style, script').forEach(elem => elem.remove());
						return doc.body.innerText
					})
					content = hrefTexts.join("\n\n") + "\n\n" + content
					saveAsText(fileName, content);
				})
			})
		} else {
			saveAsText(fileName, content);
		}
	};
	document.body.appendChild(img);
	return img
}

addButtonToBody();